#define CONST_BIT_ADDRESS(address, bit) (reinterpret_cast<u8 *>(address) + (bit) / 8)                                                                                                                  // Colorize: green
#define CONST_BIT_IN_U8(bit)            (1ULL << (7 - ((bit) & 7)))                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
