// Verified with X86RegistersSpecVerifier [BEGIN] // https://cdrdv2.intel.com/v1/dl/getContent/671436                                                                                                    // Colorize: green
typedef good_U64 x86_flags;                                                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class X86Flag: x86_flags                                                                                                                                                                            // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                                   = 0,                                                                                                                                                          // Colorize: green
    CARRY_FLAG                             = (1ULL << 0),  // Set if an arithmetic operation generates a carry or a borrow out of the mostsignificant bit of the result; cleared otherwise               // Colorize: green
    FIXED                                  = (1ULL << 1),  // Reserved, always 1 in EFLAGS                                                                                                               // Colorize: green
    PARITY_FLAG                            = (1ULL << 2),  // Set if the least-significant byte of the result contains an even number of 1 bits; cleared otherwise                                       // Colorize: green
    AUXILIARY_CARRY_FLAG                   = (1ULL << 4),  // Set if an arithmetic operation generates a carry or a borrow out of bit 3 of the result; cleared otherwise                                 // Colorize: green
    ZERO_FLAG                              = (1ULL << 6),  // Set if the result is zero; cleared otherwise                                                                                               // Colorize: green
    SIGN_FLAG                              = (1ULL << 7),  // Set equal to the most-significant bit of the result, which is the sign bit of a signed integer                                             // Colorize: green
    TRAP_FLAG                              = (1ULL << 8),  // Set to enable single-step mode for debugging; clear to disable single-step mode                                                            // Colorize: green
    INTERRUPT_ENABLE_FLAG                  = (1ULL << 9),  // Controls the response of the processor to maskable interrupt requests                                                                      // Colorize: green
    DIRECTION_FLAG                         = (1ULL << 10), // Controls string instructions (MOVS, CMPS, SCAS, LODS, and STOS). Setting the DF flag causes the string instructions to auto-decrement (to process strings from high addresses to low addresses) // Colorize: green
    OVERFLOW_FLAG                          = (1ULL << 11), // Set if the integer result is too large a positive number or too small a negative number (excluding the sign-bit) to fit in the destination operand; cleared otherwise // Colorize: green
    IO_PRIVELEGE_LEVEL_BIT_0               = (1ULL << 12), // Indicates the I/O privilege level of the currently running program or task                                                                 // Colorize: green
    IO_PRIVELEGE_LEVEL_BIT_1               = (1ULL << 13), // Indicates the I/O privilege level of the currently running program or task                                                                 // Colorize: green
    NESTED_TASK_FLAG                       = (1ULL << 14), // Controls the chaining of interrupted and called tasks                                                                                      // Colorize: green
    RESUME_FLAG                            = (1ULL << 16), // Controls the processor's response to debug exceptions                                                                                      // Colorize: green
    VIRTUAL_8086_MODE_FLAG                 = (1ULL << 17), // Set to enable virtual-8086 mode; clear to return to protected mode without virtual-8086 mode semantics                                     // Colorize: green
    ALIGNMENT_CHECK_OR_ACCESS_CONTROL_FLAG = (1ULL << 18), // If the AM bit is set in the CR0 register, alignment checking of user-mode data accesses is enabled if and only if this flag is 1           // Colorize: green
    VIRTUAL_INTERRUPT_FLAG                 = (1ULL << 19), // Virtual image of the IF flag                                                                                                               // Colorize: green
    VIRTUAL_INTERRUPT_PENDING_FLAG         = (1ULL << 20), // Set to indicate that an interrupt is pending; clear when no interrupt is pending                                                           // Colorize: green
    IDENTIFICATION_FLAG                    = (1ULL << 21)  // The ability of a program to set or clear this flag indicates support for the CPUID instruction                                             // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
