// Verified with DmiSpecVerifier [BEGIN] // https://www.dmtf.org/sites/default/files/standards/documents/DSP0134_3.4.0.pdf                                                                               // Colorize: green
typedef good_U8 dmi_baseboard_feature_flags;                                                                                                                                                             // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class DmiBaseboardFeatureFlag: dmi_baseboard_feature_flags                                                                                                                                          // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                   = 0,                                                                                                                                                                          // Colorize: green
    MOTHERBOARD            = (1ULL << 0), // Set to 1 if the board is a hosting board (for example, a motherboard).                                                                                      // Colorize: green
    REQUIRES_DAUGHTER_CARD = (1ULL << 1), // Set to 1 if the board requires at least one daughter board or auxiliary card to function properly.                                                          // Colorize: green
    REMOVABLE              = (1ULL << 2), // Set to 1 if the board is removable; it is designed to be taken in and out of the chassis without impairing the function of the chassis.                     // Colorize: green
    REPLACEABLE            = (1ULL << 3), // Set to 1 if the board is replaceable; it is possible to replace (either as a field repair or as an upgrade) the board with a physically different board. The board is inherently removable. // Colorize: green
    HOT_SWAPPABLE          = (1ULL << 4)  // Set to 1 if the board is hot swappable; it is possible to replace the board with a physically different but equivalent board while power is applied to the board. The board is inherently replaceable and removable. // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
DEFINE_FLAGS(DmiBaseboardFeatureFlags, dmi_baseboard_feature_flags); // TEST: NO                                                                                                                         // Colorize: green
// Verified with DmiSpecVerifier [END] // https://www.dmtf.org/sites/default/files/standards/documents/DSP0134_3.4.0.pdf                                                                               // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* flagToString(DmiBaseboardFeatureFlag flag) // TEST: NO                                                                                                                               // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | flag = %u", flag)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (flag)                                                                                                                                                                                        // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiBaseboardFeatureFlag::NONE:                   return "NONE";                                                                                                                             // Colorize: green
        case DmiBaseboardFeatureFlag::MOTHERBOARD:            return "MOTHERBOARD";                                                                                                                      // Colorize: green
        case DmiBaseboardFeatureFlag::REQUIRES_DAUGHTER_CARD: return "REQUIRES_DAUGHTER_CARD";                                                                                                           // Colorize: green
        case DmiBaseboardFeatureFlag::REMOVABLE:              return "REMOVABLE";                                                                                                                        // Colorize: green
        case DmiBaseboardFeatureFlag::REPLACEABLE:            return "REPLACEABLE";                                                                                                                      // Colorize: green
        case DmiBaseboardFeatureFlag::HOT_SWAPPABLE:          return "HOT_SWAPPABLE";                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* flagToFullString(DmiBaseboardFeatureFlag flag) // TEST: NO                                                                                                                           // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | flag = %u", flag)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[30];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (dmi_baseboard_feature_flags)flag, flagToString(flag));                                                                                                                  // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
