typedef u8 dmi_baseboard_feature_flags;                                                                                                                                                                  // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class DmiBaseboardFeatureFlag: dmi_baseboard_feature_flags                                                                                                                                          // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                   = 0,                                                                                                                                                                          // Colorize: green
    MOTHERBOARD            = (1ULL << 0),                                                                                                                                                                // Colorize: green
    REQUIRES_DAUGHTER_CARD = (1ULL << 1),                                                                                                                                                                // Colorize: green
    REMOVABLE              = (1ULL << 2),                                                                                                                                                                // Colorize: green
    REPLACEABLE            = (1ULL << 3),                                                                                                                                                                // Colorize: green
    HOT_SWAPPABLE          = (1ULL << 4)                                                                                                                                                                 // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
DEFINE_FLAGS(DmiBaseboardFeatureFlags, dmi_baseboard_feature_flags); // TEST: NO                                                                                                                         // Colorize: green
