// Verified with DmiSpecVerifier [BEGIN] // https://www.dmtf.org/sites/default/files/standards/documents/DSP0134_3.4.0.pdf                                                                               // Colorize: green
enum class DmiBaseboardType: good_U8                                                                                                                                                                     // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                     = 0,                                                                                                                                                                        // Colorize: green
    UNKNOWN                  = 1,  // Unknown                                                                                                                                                             // Colorize: green
    OTHER                    = 2,  // Other                                                                                                                                                               // Colorize: green
    SERVER_BLADE             = 3,  // Server Blade                                                                                                                                                        // Colorize: green
    CONNECTIVITY_SWITCH      = 4,  // Connectivity Switch                                                                                                                                                 // Colorize: green
    SYSTEM_MANAGEMENT_MODULE = 5,  // System Management Module                                                                                                                                            // Colorize: green
    PROCESSOR_MODULE         = 6,  // Processor Module                                                                                                                                                    // Colorize: green
    IO_MODULE                = 7,  // I/O Module                                                                                                                                                          // Colorize: green
    MEMORY_MODULE            = 8,  // Memory Module                                                                                                                                                       // Colorize: green
    DAUGHTER_BOARD           = 9,  // Daughter board                                                                                                                                                      // Colorize: green
    MOTHERBOARD              = 10, // Motherboard (includes processor, memory, and I/O)                                                                                                                  // Colorize: green
    PROCESSOR_MEMORY_MODULE  = 11, // Processor/Memory Module                                                                                                                                            // Colorize: green
    PROCESSOR_IO_MODULE      = 12, // Processor/IO Module                                                                                                                                                // Colorize: green
    INTERCONNECT_BOARD       = 13  // Interconnect board                                                                                                                                                 // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
// Verified with DmiSpecVerifier [END] // https://www.dmtf.org/sites/default/files/standards/documents/DSP0134_3.4.0.pdf                                                                                 // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToString(DmiBaseboardType type) // TEST: NO                                                                                                                                      // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | type = %u", type)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (type)                                                                                                                                                                                        // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiBaseboardType::NONE:                     return "NONE";                                                                                                                                  // Colorize: green
        case DmiBaseboardType::UNKNOWN:                  return "UNKNOWN";                                                                                                                               // Colorize: green
        case DmiBaseboardType::OTHER:                    return "OTHER";                                                                                                                                 // Colorize: green
        case DmiBaseboardType::SERVER_BLADE:             return "SERVER_BLADE";                                                                                                                          // Colorize: green
        case DmiBaseboardType::CONNECTIVITY_SWITCH:      return "CONNECTIVITY_SWITCH";                                                                                                                   // Colorize: green
        case DmiBaseboardType::SYSTEM_MANAGEMENT_MODULE: return "SYSTEM_MANAGEMENT_MODULE";                                                                                                              // Colorize: green
        case DmiBaseboardType::PROCESSOR_MODULE:         return "PROCESSOR_MODULE";                                                                                                                      // Colorize: green
        case DmiBaseboardType::IO_MODULE:                return "IO_MODULE";                                                                                                                             // Colorize: green
        case DmiBaseboardType::MEMORY_MODULE:            return "MEMORY_MODULE";                                                                                                                         // Colorize: green
        case DmiBaseboardType::DAUGHTER_BOARD:           return "DAUGHTER_BOARD";                                                                                                                        // Colorize: green
        case DmiBaseboardType::MOTHERBOARD:              return "MOTHERBOARD";                                                                                                                           // Colorize: green
        case DmiBaseboardType::PROCESSOR_MEMORY_MODULE:  return "PROCESSOR_MEMORY_MODULE";                                                                                                               // Colorize: green
        case DmiBaseboardType::PROCESSOR_IO_MODULE:      return "PROCESSOR_IO_MODULE";                                                                                                                   // Colorize: green
        case DmiBaseboardType::INTERCONNECT_BOARD:       return "INTERCONNECT_BOARD";                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToFullString(DmiBaseboardType type) // TEST: NO                                                                                                                                  // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | type = %u", type)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[32];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (u8)type, enumToString(type));                                                                                                                                           // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
