typedef good_U8 dmi_bios_characteristics_bios_reserved_flags;                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class DmiBiosCharacteristicsBiosReservedFlag: dmi_bios_characteristics_bios_reserved_flags                                                                                                          // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                           = 0,                                                                                                                                                                  // Colorize: green
    ACPI_SUPPORTED                 = (1ULL << 0), // ACPI is supported.                                                                                                                                  // Colorize: green
    USB_LEGACY_SUPPORTED           = (1ULL << 1), // USB Legacy is supported.                                                                                                                            // Colorize: green
    AGP_SUPPORTED                  = (1ULL << 2), // AGP is supported.                                                                                                                                   // Colorize: green
    I2O_BOOT_SUPPORTED             = (1ULL << 3), // I2O boot is supported.                                                                                                                              // Colorize: green
    LS120_BOOT_SUPPORTED           = (1ULL << 4), // LS-120 SuperDisk boot is supported.                                                                                                                 // Colorize: green
    ATAPI_ZIP_DRIVE_BOOT_SUPPORTED = (1ULL << 5), // ATAPI ZIP drive boot is supported.                                                                                                                  // Colorize: green
    BOOT_1394_SUPPORTED            = (1ULL << 6), // 1394 boot is supported.                                                                                                                             // Colorize: green
    SMART_BATTERY_SUPPORTED        = (1ULL << 7)  // Smart battery is supported.                                                                                                                         // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
DEFINE_FLAGS(DmiBiosCharacteristicsBiosReservedFlags, dmi_bios_characteristics_bios_reserved_flags); // TEST: NO                                                                                         // Colorize: green
