inline const char8* flagToString(DmiBiosCharacteristicsBiosReservedFlag flag) // TEST: NO                                                                                                                // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | flag = %u", flag)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (flag)                                                                                                                                                                                        // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiBiosCharacteristicsBiosReservedFlag::NONE:                           return "NONE";                                                                                                      // Colorize: green
        case DmiBiosCharacteristicsBiosReservedFlag::ACPI_SUPPORTED:                 return "ACPI_SUPPORTED";                                                                                            // Colorize: green
        case DmiBiosCharacteristicsBiosReservedFlag::USB_LEGACY_SUPPORTED:           return "USB_LEGACY_SUPPORTED";                                                                                      // Colorize: green
        case DmiBiosCharacteristicsBiosReservedFlag::AGP_SUPPORTED:                  return "AGP_SUPPORTED";                                                                                             // Colorize: green
        case DmiBiosCharacteristicsBiosReservedFlag::I2O_BOOT_SUPPORTED:             return "I2O_BOOT_SUPPORTED";                                                                                        // Colorize: green
        case DmiBiosCharacteristicsBiosReservedFlag::LS120_BOOT_SUPPORTED:           return "LS120_BOOT_SUPPORTED";                                                                                      // Colorize: green
        case DmiBiosCharacteristicsBiosReservedFlag::ATAPI_ZIP_DRIVE_BOOT_SUPPORTED: return "ATAPI_ZIP_DRIVE_BOOT_SUPPORTED";                                                                            // Colorize: green
        case DmiBiosCharacteristicsBiosReservedFlag::BOOT_1394_SUPPORTED:            return "BOOT_1394_SUPPORTED";                                                                                       // Colorize: green
        case DmiBiosCharacteristicsBiosReservedFlag::SMART_BATTERY_SUPPORTED:        return "SMART_BATTERY_SUPPORTED";                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
