// Verified with DmiSpecVerifier [BEGIN] // https://www.dmtf.org/sites/default/files/standards/documents/DSP0134_3.4.0.pdf                                                                               // Colorize: green
typedef good_U64 dmi_bios_characteristics_flags;                                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class DmiBiosCharacteristicsFlag: dmi_bios_characteristics_flags                                                                                                                                    // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                               = 0,                                                                                                                                                              // Colorize: green
    UNKNOWN                            = (1ULL << 2),  // Unknown.                                                                                                                                       // Colorize: green
    BIOS_CHARACTERISTICS_NOT_SUPPORTED = (1ULL << 3),  // BIOS Characteristics are not supported.                                                                                                        // Colorize: green
    ISA_SUPPORTED                      = (1ULL << 4),  // ISA is supported.                                                                                                                              // Colorize: green
    MCA_SUPPORTED                      = (1ULL << 5),  // MCA is supported.                                                                                                                              // Colorize: green
    EISA_SUPPORTED                     = (1ULL << 6),  // EISA is supported.                                                                                                                             // Colorize: green
    PCI_SUPPORTED                      = (1ULL << 7),  // PCI is supported.                                                                                                                              // Colorize: green
    PCMCIA_SUPPORTED                   = (1ULL << 8),  // PC card (PCMCIA) is supported.                                                                                                                 // Colorize: green
    PLUG_AND_PLAY_SUPPORTED            = (1ULL << 9),  // Plug and Play is supported.                                                                                                                    // Colorize: green
    APM_SUPPORTED                      = (1ULL << 10), // APM is supported.                                                                                                                              // Colorize: green
    BIOS_UPGRADABLE                    = (1ULL << 11), // BIOS is upgradeable (Flash).                                                                                                                   // Colorize: green
    BIOS_SHADOWING_ALLOWED             = (1ULL << 12), // BIOS shadowing is allowed.                                                                                                                     // Colorize: green
    VL_VESA_SUPPORTED                  = (1ULL << 13), // VL-VESA is supported.                                                                                                                          // Colorize: green
    ESCD_SUPPORT_AVAILABLE             = (1ULL << 14), // ESCD support is available.                                                                                                                     // Colorize: green
    BOOT_FROM_CD_SUPPORTED             = (1ULL << 15), // Boot from CD is supported.                                                                                                                     // Colorize: green
    SELECTABLE_BOOT_SUPPORTED          = (1ULL << 16), // Selectable boot is supported.                                                                                                                  // Colorize: green
    BIOS_ROM_SOCKETED                  = (1ULL << 17), // BIOS ROM is socketed (e.g. PLCC or SOP socket).                                                                                                // Colorize: green
    BOOT_FROM_PCMCIA_SUPPORTED         = (1ULL << 18), // Boot from PC card (PCMCIA) is supported.                                                                                                       // Colorize: green
    EDD_SPECIFICATION_SUPPORTED        = (1ULL << 19), // EDD specification is supported.                                                                                                                // Colorize: green
    JAPANESE_NEC_FLOPPY_SUPPORTED      = (1ULL << 20), // Int 13h — Japanese floppy for NEC 9800 1.2 MB (3.5", 1K bytes/sector, 360 RPM) is supported.                                                   // Colorize: green
    JAPANESE_TOSHIBA_FLOPPY_SUPPORTED  = (1ULL << 21), // Int 13h — Japanese floppy for Toshiba 1.2 MB (3.5", 360 RPM) is supported.                                                                     // Colorize: green
    FLOPPY_525_360_SUPPORTED           = (1ULL << 22), // Int 13h — 5.25" / 360 KB floppy services are supported.                                                                                        // Colorize: green
    FLOPPY_525_12_SUPPORTED            = (1ULL << 23), // Int 13h — 5.25" /1.2 MB floppy services are supported.                                                                                         // Colorize: green
    FLOPPY_35_720_SUPPORTED            = (1ULL << 24), // Int 13h — 3.5" / 720 KB floppy services are supported.                                                                                         // Colorize: green
    FLOPPY_35_288_SUPPORTED            = (1ULL << 25), // Int 13h — 3.5" / 2.88 MB floppy services are supported.                                                                                        // Colorize: green
    PRINT_SCREEN_SUPPORTED             = (1ULL << 26), // Int 5h, print screen Service is supported.                                                                                                     // Colorize: green
    KEYBOARD_8042_SUPPORTED            = (1ULL << 27), // Int 9h, 8042 keyboard services are supported.                                                                                                  // Colorize: green
    SERIAL_SUPPORTED                   = (1ULL << 28), // Int 14h, serial services are supported.                                                                                                        // Colorize: green
    PRINTER_SUPPORTED                  = (1ULL << 29), // Int 17h, printer services are supported.                                                                                                       // Colorize: green
    CGA_MONO_SUPPORTED                 = (1ULL << 30), // Int 10h, CGA/Mono Video Services are supported.                                                                                                // Colorize: green
    NEC_PC_98                          = (1ULL << 31)  // NEC PC-98.                                                                                                                                     // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
