        case DmiBiosCharacteristicsFlag::MCA_SUPPORTED:                      return "MCA_SUPPORTED";                                                                                                     // Colorize: green
        case DmiBiosCharacteristicsFlag::EISA_SUPPORTED:                     return "EISA_SUPPORTED";                                                                                                    // Colorize: green
        case DmiBiosCharacteristicsFlag::PCI_SUPPORTED:                      return "PCI_SUPPORTED";                                                                                                     // Colorize: green
        case DmiBiosCharacteristicsFlag::PCMCIA_SUPPORTED:                   return "PCMCIA_SUPPORTED";                                                                                                  // Colorize: green
        case DmiBiosCharacteristicsFlag::PLUG_AND_PLAY_SUPPORTED:            return "PLUG_AND_PLAY_SUPPORTED";                                                                                           // Colorize: green
        case DmiBiosCharacteristicsFlag::APM_SUPPORTED:                      return "APM_SUPPORTED";                                                                                                     // Colorize: green
        case DmiBiosCharacteristicsFlag::BIOS_UPGRADABLE:                    return "BIOS_UPGRADABLE";                                                                                                   // Colorize: green
        case DmiBiosCharacteristicsFlag::BIOS_SHADOWING_ALLOWED:             return "BIOS_SHADOWING_ALLOWED";                                                                                            // Colorize: green
        case DmiBiosCharacteristicsFlag::VL_VESA_SUPPORTED:                  return "VL_VESA_SUPPORTED";                                                                                                 // Colorize: green
        case DmiBiosCharacteristicsFlag::ESCD_SUPPORT_AVAILABLE:             return "ESCD_SUPPORT_AVAILABLE";                                                                                            // Colorize: green
        case DmiBiosCharacteristicsFlag::BOOT_FROM_CD_SUPPORTED:             return "BOOT_FROM_CD_SUPPORTED";                                                                                            // Colorize: green
        case DmiBiosCharacteristicsFlag::SELECTABLE_BOOT_SUPPORTED:          return "SELECTABLE_BOOT_SUPPORTED";                                                                                         // Colorize: green
        case DmiBiosCharacteristicsFlag::BIOS_ROM_SOCKETED:                  return "BIOS_ROM_SOCKETED";                                                                                                 // Colorize: green
        case DmiBiosCharacteristicsFlag::BOOT_FROM_PCMCIA_SUPPORTED:         return "BOOT_FROM_PCMCIA_SUPPORTED";                                                                                        // Colorize: green
        case DmiBiosCharacteristicsFlag::EDD_SPECIFICATION_SUPPORTED:        return "EDD_SPECIFICATION_SUPPORTED";                                                                                       // Colorize: green
        case DmiBiosCharacteristicsFlag::JAPANESE_NEC_FLOPPY_SUPPORTED:      return "JAPANESE_NEC_FLOPPY_SUPPORTED";                                                                                     // Colorize: green
        case DmiBiosCharacteristicsFlag::JAPANESE_TOSHIBA_FLOPPY_SUPPORTED:  return "JAPANESE_TOSHIBA_FLOPPY_SUPPORTED";                                                                                 // Colorize: green
        case DmiBiosCharacteristicsFlag::FLOPPY_525_360_SUPPORTED:           return "FLOPPY_525_360_SUPPORTED";                                                                                          // Colorize: green
        case DmiBiosCharacteristicsFlag::FLOPPY_525_12_SUPPORTED:            return "FLOPPY_525_12_SUPPORTED";                                                                                           // Colorize: green
        case DmiBiosCharacteristicsFlag::FLOPPY_35_720_SUPPORTED:            return "FLOPPY_35_720_SUPPORTED";                                                                                           // Colorize: green
        case DmiBiosCharacteristicsFlag::FLOPPY_35_288_SUPPORTED:            return "FLOPPY_35_288_SUPPORTED";                                                                                           // Colorize: green
        case DmiBiosCharacteristicsFlag::PRINT_SCREEN_SUPPORTED:             return "PRINT_SCREEN_SUPPORTED";                                                                                            // Colorize: green
        case DmiBiosCharacteristicsFlag::KEYBOARD_8042_SUPPORTED:            return "KEYBOARD_8042_SUPPORTED";                                                                                           // Colorize: green
        case DmiBiosCharacteristicsFlag::SERIAL_SUPPORTED:                   return "SERIAL_SUPPORTED";                                                                                                  // Colorize: green
        case DmiBiosCharacteristicsFlag::PRINTER_SUPPORTED:                  return "PRINTER_SUPPORTED";                                                                                                 // Colorize: green
        case DmiBiosCharacteristicsFlag::CGA_MONO_SUPPORTED:                 return "CGA_MONO_SUPPORTED";                                                                                                // Colorize: green
