                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
struct DmiBiosExtendedRomSize                                                                                                                                                                            // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    inline good_U64 size() const                                                                                                                                                                                     // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        // COMMON_LT(("")); // Commented to avoid too frequent logs                                                                                                                                      // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        switch (unit)                                                                                                                                                                                    // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            case DmiBiosExtendedRomSizeUnit::MEGABYTES: return static_cast<good_U64>(value) * MB;                                                                                                        // Colorize: green
            case DmiBiosExtendedRomSizeUnit::GIGABYTES: return static_cast<good_U64>(value) * GB;                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            default:                                                                                                                                                                                     // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                COMMON_LF(("Unknown BIOS extended rom size unit %s, %s:%u", enumToFullString(unit), __FILE__, __LINE__));                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                return 0;                                                                                                                                                                                // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
            break;                                                                                                                                                                                       // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        return value;                                                                                                                                                                                    // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    union                                                                                                                                                                                                // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        struct                                                                                                                                                                                           // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            good_U16                   value: 14;                                                                                                                                                        // Colorize: green
            DmiBiosExtendedRomSizeUnit unit:  2;                                                                                                                                                         // Colorize: green
        };                                                                                                                                                                                               // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        good_U16 value16;                                                                                                                                                                                // Colorize: green
    };                                                                                                                                                                                                   // Colorize: green
} __attribute__((packed));                                                                                                                                                                               // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
