                                                                                                                                                                                                         // Colorize: green
enum class DmiBits32MemoryErrorInformationErrorType: u8                                                                                                                                                  // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                        = 0,                                                                                                                                                                     // Colorize: green
    OTHER                       = 1,                                                                                                                                                                     // Colorize: green
    UNKNOWN                     = 2,                                                                                                                                                                     // Colorize: green
    OK                          = 3,                                                                                                                                                                     // Colorize: green
    BAD_READ                    = 4,                                                                                                                                                                     // Colorize: green
    PARITY_ERROR                = 5,                                                                                                                                                                     // Colorize: green
    SINGLE_BIT_ERROR            = 6,                                                                                                                                                                     // Colorize: green
    DOUBLE_BIT_ERROR            = 7,                                                                                                                                                                     // Colorize: green
    MULTI_BIT_ERROR             = 8,                                                                                                                                                                     // Colorize: green
    NIBBLE_ERROR                = 9,                                                                                                                                                                     // Colorize: green
    CHECKSUM_ERROR              = 10,                                                                                                                                                                    // Colorize: green
    CRC_ERROR                   = 11,                                                                                                                                                                    // Colorize: green
    CORRECTED_SINGLE_BIT_ERROR  = 12,                                                                                                                                                                    // Colorize: green
    CORRECTED_ERROR             = 13,                                                                                                                                                                    // Colorize: green
    UNCORRECTABLE_ERROR         = 14                                                                                                                                                                     // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToString(DmiBits32MemoryErrorInformationErrorType type) // TEST: NO                                                                                                              // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | type = %u", type)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (type)                                                                                                                                                                                        // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::NONE:                       return "NONE";                                                                                                        // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::OTHER:                      return "OTHER";                                                                                                       // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::UNKNOWN:                    return "UNKNOWN";                                                                                                     // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::OK:                         return "OK";                                                                                                          // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::BAD_READ:                   return "BAD_READ";                                                                                                    // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::PARITY_ERROR:               return "PARITY_ERROR";                                                                                                // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::SINGLE_BIT_ERROR:           return "SINGLE_BIT_ERROR";                                                                                            // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::DOUBLE_BIT_ERROR:           return "DOUBLE_BIT_ERROR";                                                                                            // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::MULTI_BIT_ERROR:            return "MULTI_BIT_ERROR";                                                                                             // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::NIBBLE_ERROR:               return "NIBBLE_ERROR";                                                                                                // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::CHECKSUM_ERROR:             return "CHECKSUM_ERROR";                                                                                              // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::CRC_ERROR:                  return "CRC_ERROR";                                                                                                   // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::CORRECTED_SINGLE_BIT_ERROR: return "CORRECTED_SINGLE_BIT_ERROR";                                                                                  // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::CORRECTED_ERROR:            return "CORRECTED_ERROR";                                                                                             // Colorize: green
        case DmiBits32MemoryErrorInformationErrorType::UNCORRECTABLE_ERROR:        return "UNCORRECTABLE_ERROR";                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToFullString(DmiBits32MemoryErrorInformationErrorType type) // TEST: NO                                                                                                          // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | type = %u", type)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[34];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (u8)type, enumToString(type));                                                                                                                                           // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
