                                                                                                                                                                                                         // Colorize: green
enum class DmiCacheOperationalMode: u8                                                                                                                                                                   // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    WRITE_THROUGH              = 0,                                                                                                                                                                      // Colorize: green
    WRITE_BACK                 = 1,                                                                                                                                                                      // Colorize: green
    VARIES_WITH_MEMORY_ADDRESS = 2,                                                                                                                                                                      // Colorize: green
    UNKNOWN                    = 3                                                                                                                                                                       // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToString(DmiCacheOperationalMode mode) // TEST: NO                                                                                                                               // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | mode = %u", mode)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (mode)                                                                                                                                                                                        // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiCacheOperationalMode::WRITE_THROUGH:              return "WRITE_THROUGH";                                                                                                                // Colorize: green
        case DmiCacheOperationalMode::WRITE_BACK:                 return "WRITE_BACK";                                                                                                                   // Colorize: green
        case DmiCacheOperationalMode::VARIES_WITH_MEMORY_ADDRESS: return "VARIES_WITH_MEMORY_ADDRESS";                                                                                                   // Colorize: green
        case DmiCacheOperationalMode::UNKNOWN:                    return "UNKNOWN";                                                                                                                      // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToFullString(DmiCacheOperationalMode mode) // TEST: NO                                                                                                                           // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | mode = %u", mode)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[34];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (u8)mode, enumToString(mode));                                                                                                                                           // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
