enum class DmiMemoryDeviceOperatingModeCapabilityFlag: dmi_memory_device_operating_mode_capability_flags                                                                                                 // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                               = 0,                                                                                                                                                              // Colorize: green
    OTHER                              = (1ULL << 1),                                                                                                                                                    // Colorize: green
    UNKNOWN                            = (1ULL << 2),                                                                                                                                                    // Colorize: green
    VOLATILE_MEMORY                    = (1ULL << 3),                                                                                                                                                    // Colorize: green
    BYTE_ACCESSIBLE_PERSISTENT_MEMORY  = (1ULL << 4),                                                                                                                                                    // Colorize: green
    BLOCK_ACCESSIBLE_PERSISTENT_MEMORY = (1ULL << 5)                                                                                                                                                     // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
