inline const char8* flagToString(DmiMemoryDeviceOperatingModeCapabilityFlag flag) // TEST: NO                                                                                                            // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | flag = %u", flag)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (flag)                                                                                                                                                                                        // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiMemoryDeviceOperatingModeCapabilityFlag::NONE:                               return "NONE";                                                                                              // Colorize: green
        case DmiMemoryDeviceOperatingModeCapabilityFlag::OTHER:                              return "OTHER";                                                                                             // Colorize: green
        case DmiMemoryDeviceOperatingModeCapabilityFlag::UNKNOWN:                            return "UNKNOWN";                                                                                           // Colorize: green
        case DmiMemoryDeviceOperatingModeCapabilityFlag::VOLATILE_MEMORY:                    return "VOLATILE_MEMORY";                                                                                   // Colorize: green
        case DmiMemoryDeviceOperatingModeCapabilityFlag::BYTE_ACCESSIBLE_PERSISTENT_MEMORY:  return "BYTE_ACCESSIBLE_PERSISTENT_MEMORY";                                                                 // Colorize: green
        case DmiMemoryDeviceOperatingModeCapabilityFlag::BLOCK_ACCESSIBLE_PERSISTENT_MEMORY: return "BLOCK_ACCESSIBLE_PERSISTENT_MEMORY";                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
