                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
#include <com/ngos/shared/common/log/assert.h>                                                                                                                                                           // Colorize: green
#include <com/ngos/shared/common/log/log.h>                                                                                                                                                              // Colorize: green
#include <com/ngos/shared/common/ngos/types.h>                                                                                                                                                           // Colorize: green
#include <com/ngos/shared/common/printf/printf.h>                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class DmiMemoryDeviceTechnology: u8                                                                                                                                                                 // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                    = 0,                                                                                                                                                                         // Colorize: green
    OTHER                   = 1,                                                                                                                                                                         // Colorize: green
    UNKNOWN                 = 2,                                                                                                                                                                         // Colorize: green
    DRAM                    = 3,                                                                                                                                                                         // Colorize: green
    NVDIMM_N                = 4,                                                                                                                                                                         // Colorize: green
    NVDIMM_F                = 5,                                                                                                                                                                         // Colorize: green
    NVDIMM_P                = 6,                                                                                                                                                                         // Colorize: green
    INTEL_PERSISTENT_MEMORY = 7                                                                                                                                                                          // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToString(DmiMemoryDeviceTechnology technology) // TEST: NO                                                                                                                       // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | technology = %u", technology)); // Commented to avoid bad looking logs                                                                                                             // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (technology)                                                                                                                                                                                  // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiMemoryDeviceTechnology::NONE:                    return "NONE";                                                                                                                          // Colorize: green
        case DmiMemoryDeviceTechnology::OTHER:                   return "OTHER";                                                                                                                         // Colorize: green
        case DmiMemoryDeviceTechnology::UNKNOWN:                 return "UNKNOWN";                                                                                                                       // Colorize: green
        case DmiMemoryDeviceTechnology::DRAM:                    return "DRAM";                                                                                                                          // Colorize: green
        case DmiMemoryDeviceTechnology::NVDIMM_N:                return "NVDIMM_N";                                                                                                                      // Colorize: green
        case DmiMemoryDeviceTechnology::NVDIMM_F:                return "NVDIMM_F";                                                                                                                      // Colorize: green
        case DmiMemoryDeviceTechnology::NVDIMM_P:                return "NVDIMM_P";                                                                                                                      // Colorize: green
        case DmiMemoryDeviceTechnology::INTEL_PERSISTENT_MEMORY: return "INTEL_PERSISTENT_MEMORY";                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToFullString(DmiMemoryDeviceTechnology technology) // TEST: NO                                                                                                                   // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | technology = %u", technology)); // Commented to avoid bad looking logs                                                                                                             // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[31];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (u8)technology, enumToString(technology));                                                                                                                               // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
