                                                                                                                                                                                                         // Colorize: green
enum class DmiPhysicalMemoryArrayLocation: u8                                                                                                                                                            // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                           = 0,                                                                                                                                                                  // Colorize: green
    OTHER                          = 0x01,                                                                                                                                                               // Colorize: green
    UNKNOWN                        = 0x02,                                                                                                                                                               // Colorize: green
    SYSTEM_BOARD                   = 0x03,                                                                                                                                                               // Colorize: green
    ISA_ADDON_CARD                 = 0x04,                                                                                                                                                               // Colorize: green
    EISA_ADDON_CARD                = 0x05,                                                                                                                                                               // Colorize: green
    PCI_ADDON_CARD                 = 0x06,                                                                                                                                                               // Colorize: green
    MCA_ADDON_CARD                 = 0x07,                                                                                                                                                               // Colorize: green
    PCMCIA_ADDON_CARD              = 0x08,                                                                                                                                                               // Colorize: green
    PROPRIETARY_ADDON_CARD         = 0x09,                                                                                                                                                               // Colorize: green
    NU_BUS                         = 0x0A,                                                                                                                                                               // Colorize: green
    PC98_C20_ADDON_CARD            = 0xA0,                                                                                                                                                               // Colorize: green
    PC98_C24_ADDON_CARD            = 0xA1,                                                                                                                                                               // Colorize: green
    PC98_E_ADDON_CARD              = 0xA2,                                                                                                                                                               // Colorize: green
    PC98_LOCAL_BUS_ADDON_CARD      = 0xA3,                                                                                                                                                               // Colorize: green
    CXL_FLEXBUS_1_0_BUS_ADDON_CARD = 0xA4                                                                                                                                                                // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToString(DmiPhysicalMemoryArrayLocation location) // TEST: NO                                                                                                                    // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | location = %u", location)); // Commented to avoid bad looking logs                                                                                                                 // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (location)                                                                                                                                                                                    // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiPhysicalMemoryArrayLocation::NONE:                           return "NONE";                                                                                                              // Colorize: green
        case DmiPhysicalMemoryArrayLocation::OTHER:                          return "OTHER";                                                                                                             // Colorize: green
        case DmiPhysicalMemoryArrayLocation::UNKNOWN:                        return "UNKNOWN";                                                                                                           // Colorize: green
        case DmiPhysicalMemoryArrayLocation::SYSTEM_BOARD:                   return "SYSTEM_BOARD";                                                                                                      // Colorize: green
        case DmiPhysicalMemoryArrayLocation::ISA_ADDON_CARD:                 return "ISA_ADDON_CARD";                                                                                                    // Colorize: green
        case DmiPhysicalMemoryArrayLocation::EISA_ADDON_CARD:                return "EISA_ADDON_CARD";                                                                                                   // Colorize: green
        case DmiPhysicalMemoryArrayLocation::PCI_ADDON_CARD:                 return "PCI_ADDON_CARD";                                                                                                    // Colorize: green
        case DmiPhysicalMemoryArrayLocation::MCA_ADDON_CARD:                 return "MCA_ADDON_CARD";                                                                                                    // Colorize: green
        case DmiPhysicalMemoryArrayLocation::PCMCIA_ADDON_CARD:              return "PCMCIA_ADDON_CARD";                                                                                                 // Colorize: green
        case DmiPhysicalMemoryArrayLocation::PROPRIETARY_ADDON_CARD:         return "PROPRIETARY_ADDON_CARD";                                                                                            // Colorize: green
        case DmiPhysicalMemoryArrayLocation::NU_BUS:                         return "NU_BUS";                                                                                                            // Colorize: green
        case DmiPhysicalMemoryArrayLocation::PC98_C20_ADDON_CARD:            return "PC98_C20_ADDON_CARD";                                                                                               // Colorize: green
        case DmiPhysicalMemoryArrayLocation::PC98_C24_ADDON_CARD:            return "PC98_C24_ADDON_CARD";                                                                                               // Colorize: green
        case DmiPhysicalMemoryArrayLocation::PC98_E_ADDON_CARD:              return "PC98_E_ADDON_CARD";                                                                                                 // Colorize: green
        case DmiPhysicalMemoryArrayLocation::PC98_LOCAL_BUS_ADDON_CARD:      return "PC98_LOCAL_BUS_ADDON_CARD";                                                                                         // Colorize: green
        case DmiPhysicalMemoryArrayLocation::CXL_FLEXBUS_1_0_BUS_ADDON_CARD: return "CXL_FLEXBUS_1_0_BUS_ADDON_CARD";                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToFullString(DmiPhysicalMemoryArrayLocation location) // TEST: NO                                                                                                                // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | location = %u", location)); // Commented to avoid bad looking logs                                                                                                                 // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[38];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (u8)location, enumToString(location));                                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
