typedef u16 dmi_processor_characteristics_flags;                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class DmiProcessorCharacteristicsFlag: dmi_processor_characteristics_flags                                                                                                                          // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                      = 0,                                                                                                                                                                       // Colorize: green
    UNKNOWN                   = BIT_1,                                                                                                                                                             // Colorize: green
    SUPPORT_64_BIT            = BIT_2,                                                                                                                                                             // Colorize: green
    MULTI_CORE                = BIT_3,                                                                                                                                                             // Colorize: green
    HARDWARE_THREAD           = BIT_4,                                                                                                                                                             // Colorize: green
    EXECUTE_PROTECTION        = BIT_5,                                                                                                                                                             // Colorize: green
    ENHANCED_VIRTUALIZATION   = BIT_6,                                                                                                                                                             // Colorize: green
    POWER_PERFORMANCE_CONTROL = BIT_7,                                                                                                                                                             // Colorize: green
    SUPPORT_128_BIT           = BIT_8                                                                                                                                                              // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
DEFINE_FLAGS(DmiProcessorCharacteristicsFlags, dmi_processor_characteristics_flags); // TEST: NO                                                                                                         // Colorize: green
