#define COM_NGOS_SHARED_COMMON_DMI_ENTRY_LIB_DMIPROCESSORSTATUS_H                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
#include <com/ngos/shared/common/log/assert.h>                                                                                                                                                           // Colorize: green
#include <com/ngos/shared/common/log/log.h>                                                                                                                                                              // Colorize: green
#include <com/ngos/shared/common/ngos/types.h>                                                                                                                                                           // Colorize: green
#include <com/ngos/shared/common/printf/printf.h>                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class DmiProcessorStatus: u8                                                                                                                                                                        // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    UNKNOWN          = 0,                                                                                                                                                                                // Colorize: green
    ENABLED          = 1,                                                                                                                                                                                // Colorize: green
    DISABLED_BY_USER = 2,                                                                                                                                                                                // Colorize: green
    DISABLED_BY_BIOS = 3,                                                                                                                                                                                // Colorize: green
    IDLE             = 4,                                                                                                                                                                                // Colorize: green
    OTHER            = 7                                                                                                                                                                                 // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToString(DmiProcessorStatus status) // TEST: NO                                                                                                                                  // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | status = %u", status)); // Commented to avoid bad looking logs                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (status)                                                                                                                                                                                      // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiProcessorStatus::UNKNOWN:          return "UNKNOWN";                                                                                                                                     // Colorize: green
        case DmiProcessorStatus::ENABLED:          return "ENABLED";                                                                                                                                     // Colorize: green
        case DmiProcessorStatus::DISABLED_BY_USER: return "DISABLED_BY_USER";                                                                                                                            // Colorize: green
        case DmiProcessorStatus::DISABLED_BY_BIOS: return "DISABLED_BY_BIOS";                                                                                                                            // Colorize: green
        case DmiProcessorStatus::IDLE:             return "IDLE";                                                                                                                                        // Colorize: green
        case DmiProcessorStatus::OTHER:            return "OTHER";                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToFullString(DmiProcessorStatus status) // TEST: NO                                                                                                                              // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | status = %u", status)); // Commented to avoid bad looking logs                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[24];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (u8)status, enumToString(status));                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
#endif // COM_NGOS_SHARED_COMMON_DMI_ENTRY_LIB_DMIPROCESSORSTATUS_H                                                                                                                                      // Colorize: green
