// Verified with DmiSpecVerifier [BEGIN] // https://www.dmtf.org/sites/default/files/standards/documents/DSP0134_3.4.0.pdf                                                                               // Colorize: green
enum class DmiProcessorType: good_U8                                                                                                                                                                     // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE              = 0,                                                                                                                                                                               // Colorize: green
    OTHER             = 1, // Other                                                                                                                                                                      // Colorize: green
    UNKNOWN           = 2, // Unknown                                                                                                                                                                    // Colorize: green
    CENTRAL_PROCESSOR = 3, // Central Processor                                                                                                                                                          // Colorize: green
    MATH_PROCESSOR    = 4, // Math Processor                                                                                                                                                             // Colorize: green
    DSP_PROCESSOR     = 5, // DSP Processor                                                                                                                                                              // Colorize: green
    VIDEO_PROCESSOR   = 6  // Video Processor                                                                                                                                                            // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
// Verified with DmiSpecVerifier [END] // https://www.dmtf.org/sites/default/files/standards/documents/DSP0134_3.4.0.pdf                                                                                 // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToString(DmiProcessorType type) // TEST: NO                                                                                                                                      // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | type = %u", type)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (type)                                                                                                                                                                                        // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiProcessorType::NONE:              return "NONE";                                                                                                                                         // Colorize: green
        case DmiProcessorType::OTHER:             return "OTHER";                                                                                                                                        // Colorize: green
        case DmiProcessorType::UNKNOWN:           return "UNKNOWN";                                                                                                                                      // Colorize: green
        case DmiProcessorType::CENTRAL_PROCESSOR: return "CENTRAL_PROCESSOR";                                                                                                                            // Colorize: green
        case DmiProcessorType::MATH_PROCESSOR:    return "MATH_PROCESSOR";                                                                                                                               // Colorize: green
        case DmiProcessorType::DSP_PROCESSOR:     return "DSP_PROCESSOR";                                                                                                                                // Colorize: green
        case DmiProcessorType::VIDEO_PROCESSOR:   return "VIDEO_PROCESSOR";                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToFullString(DmiProcessorType type) // TEST: NO                                                                                                                                  // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | type = %u", type)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[25];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (u8)type, enumToString(type));                                                                                                                                           // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
