typedef u8 dmi_processor_voltage_flags;                                                                                                                                                                  // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class DmiProcessorVoltageFlag: dmi_processor_voltage_flags                                                                                                                                          // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE            = 0,                                                                                                                                                                                 // Colorize: green
    CAPABILITY_5V   = (1ULL << 0),                                                                                                                                                                       // Colorize: green
    CAPABILITY_3_3V = (1ULL << 1),                                                                                                                                                                       // Colorize: green
    CAPABILITY_2_9V = (1ULL << 2)                                                                                                                                                                        // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
DEFINE_FLAGS(DmiProcessorVoltageFlags, dmi_processor_voltage_flags); // TEST: NO                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* flagToString(DmiProcessorVoltageFlag flag) // TEST: NO                                                                                                                               // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | flag = %u", flag)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (flag)                                                                                                                                                                                        // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiProcessorVoltageFlag::NONE:            return "NONE";                                                                                                                                    // Colorize: green
        case DmiProcessorVoltageFlag::CAPABILITY_5V:   return "CAPABILITY_5V";                                                                                                                           // Colorize: green
        case DmiProcessorVoltageFlag::CAPABILITY_3_3V: return "CAPABILITY_3_3V";                                                                                                                         // Colorize: green
        case DmiProcessorVoltageFlag::CAPABILITY_2_9V: return "CAPABILITY_2_9V";                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* flagToFullString(DmiProcessorVoltageFlag flag) // TEST: NO                                                                                                                           // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | flag = %u", flag)); // Commented to avoid bad looking logs                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[23];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (dmi_processor_voltage_flags)flag, flagToString(flag));                                                                                                                  // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
