enum class DmiVoltageProbeLocation: u8                                                                                                                                                                   // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                     = 0,                                                                                                                                                                        // Colorize: green
    OTHER                    = 1,                                                                                                                                                                        // Colorize: green
    UNKNOWN                  = 2,                                                                                                                                                                        // Colorize: green
    PROCESSOR                = 3,                                                                                                                                                                        // Colorize: green
    DISK                     = 4,                                                                                                                                                                        // Colorize: green
    PERIPHERAL_BAY           = 5,                                                                                                                                                                        // Colorize: green
    SYSTEM_MANAGEMENT_MODULE = 6,                                                                                                                                                                        // Colorize: green
    MOTHERBOARD              = 7,                                                                                                                                                                        // Colorize: green
    MEMORY_MODULE            = 8,                                                                                                                                                                        // Colorize: green
    PROCESSOR_MODULE         = 9,                                                                                                                                                                        // Colorize: green
    POWER_UNIT               = 10,                                                                                                                                                                       // Colorize: green
    ADD_IN_CARD              = 11                                                                                                                                                                        // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToString(DmiVoltageProbeLocation location) // TEST: NO                                                                                                                           // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | location = %u", location)); // Commented to avoid bad looking logs                                                                                                                 // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    switch (location)                                                                                                                                                                                    // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        case DmiVoltageProbeLocation::NONE:                     return "NONE";                                                                                                                           // Colorize: green
        case DmiVoltageProbeLocation::OTHER:                    return "OTHER";                                                                                                                          // Colorize: green
        case DmiVoltageProbeLocation::UNKNOWN:                  return "UNKNOWN";                                                                                                                        // Colorize: green
        case DmiVoltageProbeLocation::PROCESSOR:                return "PROCESSOR";                                                                                                                      // Colorize: green
        case DmiVoltageProbeLocation::DISK:                     return "DISK";                                                                                                                           // Colorize: green
        case DmiVoltageProbeLocation::PERIPHERAL_BAY:           return "PERIPHERAL_BAY";                                                                                                                 // Colorize: green
        case DmiVoltageProbeLocation::SYSTEM_MANAGEMENT_MODULE: return "SYSTEM_MANAGEMENT_MODULE";                                                                                                       // Colorize: green
        case DmiVoltageProbeLocation::MOTHERBOARD:              return "MOTHERBOARD";                                                                                                                    // Colorize: green
        case DmiVoltageProbeLocation::MEMORY_MODULE:            return "MEMORY_MODULE";                                                                                                                  // Colorize: green
        case DmiVoltageProbeLocation::PROCESSOR_MODULE:         return "PROCESSOR_MODULE";                                                                                                               // Colorize: green
        case DmiVoltageProbeLocation::POWER_UNIT:               return "POWER_UNIT";                                                                                                                     // Colorize: green
        case DmiVoltageProbeLocation::ADD_IN_CARD:              return "ADD_IN_CARD";                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        default: return "UNKNOWN";                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline const char8* enumToFullString(DmiVoltageProbeLocation location) // TEST: NO                                                                                                                       // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // COMMON_LT((" | location = %u", location)); // Commented to avoid bad looking logs                                                                                                                 // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    static char8 res[32];                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    sprintf(res, "0x%02X (%s)", (u8)location, enumToString(location));                                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return res;                                                                                                                                                                                          // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
