function run_build                                                                                                                                                                                       # Colorize: green
{                                                                                                                                                                                                        # Colorize: green
    echo "Building with $1"                                                                                                                                                                              # Colorize: green
                                                                                                                                                                                                         # Colorize: green
    tools/qt/build_config_maker/build/build_config_maker ${BUILD_CONFIG} $1 >  ${BUILD_LOG} 2>&1                                                                                                         # Colorize: green
    make all                                                                >> ${BUILD_LOG} 2>&1                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
    if [ $? -ne 0 ]; then                                                                                                                                                                                # Colorize: green
        cat ${BUILD_LOG} 2>&1                                                                                                                                                                            # Colorize: green
                                                                                                                                                                                                         # Colorize: green
        cp ${BUILD_CFG_BACKUP} ${BUILD_CONFIG}                                                                                                                                                           # Colorize: green
        cd ${WORKING_DIR}/                                                                                                                                                                               # Colorize: green
                                                                                                                                                                                                         # Colorize: green
        exit 1                                                                                                                                                                                           # Colorize: green
    fi                                                                                                                                                                                                   # Colorize: green
}                                                                                                                                                                                                        # Colorize: green
