HexCharsGenerator::HexCharsGenerator()                                                                                                                                                                   // Colorize: green
    : CommonGenerator()                                                                                                                                                                                  // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // Nothing                                                                                                                                                                                           // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
bool HexCharsGenerator::generate(const QString &path)                                                                                                                                                    // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    QStringList lines;                                                                                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    lines.append("#include <com/ngos/shared/common/hex/hex.h>");                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    addThreeBlankLines(lines);                                                                                                                                                                           // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    const char *digits = "0123456789ABCDEF";                                                                                                                                                             // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    lines.append("u16 Hex::sHexChars[256] =");                                                                                                                                                           // Colorize: green
    lines.append("{"); // Ignore CppSingleCharVerifier                                                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    for (qint64 i = 0; i < 16; ++i)                                                                                                                                                                      // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        QString line;                                                                                                                                                                                    // Colorize: green
        QString comment;                                                                                                                                                                                 // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        for (qint64 j = 0; j < 16; ++j)                                                                                                                                                                  // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            if (j > 0)                                                                                                                                                                                   // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                line.append(", ");                                                                                                                                                                       // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            line.append(QString("0x%1%2")                                                                                                                                                                // Colorize: green
                                .arg((quint8)digits[j], 2, 16, QChar('0'))                                                                                                                               // Colorize: green
                                .arg((quint8)digits[i], 2, 16, QChar('0'))                                                                                                                               // Colorize: green
            );                                                                                                                                                                                           // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            comment.append(digits[i]);                                                                                                                                                                   // Colorize: green
            comment.append(digits[j]);                                                                                                                                                                   // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        lines.append(QString("    %1%2 // %3")                                                                                                                                                           // Colorize: green
                                .arg(line)                                                                                                                                                               // Colorize: green
                                .arg(i < 15 ? ',' : ' ')                                                                                                                                                 // Colorize: green
                                .arg(comment)                                                                                                                                                            // Colorize: green
        );                                                                                                                                                                                               // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    lines.append("};");                                                                                                                                                                                  // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return save(path + FILE_PATH, lines);                                                                                                                                                                // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
