#define FILE_PATH "/src/com/ngos/configure/other/asm/generated/asmoffsets.h"                                                                                                                             // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
#define ADD_ASM_OFFSET(name, align, type, field) \
        lines.append(QString("#define %1 0x%2") \
                                .arg(#name, -align, QChar(' ')) \
                                .arg((quint64)(&((type *)nullptr)->field), 8, 16, QChar('0')) \
        );                                                                                                                                                                                               // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
AsmOffsetsGenerator::AsmOffsetsGenerator()                                                                                                                                                               // Colorize: green
    : ConfigureGenerator()                                                                                                                                                                               // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // Nothing                                                                                                                                                                                           // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
bool AsmOffsetsGenerator::generate(const QString &path)                                                                                                                                                  // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    QStringList lines;                                                                                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    ADD_ASM_OFFSET(ASM_OFFSET_BOOT_PARAM_KERNEL_LOCATION, 37, BootParams, header.kernelLocation);                                                                                                        // Colorize: green
    ADD_ASM_OFFSET(ASM_OFFSET_BOOT_PARAM_KERNEL_SIZE,     37, BootParams, header.kernelSize);                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    lines.append("");                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    ADD_ASM_OFFSET(ASM_OFFSET_KERNEL_DESCRIPTOR_CONTENT,  37, KernelDescriptor, content);                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return save(path + FILE_PATH, lines);                                                                                                                                                                // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
