CppSwitchVerifier::CppSwitchVerifier()                                                                                                                                                                   // Colorize: green
    : BaseCodeVerifier(VERIFICATION_COMMON_CPP)                                                                                                                                                          // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // Nothing                                                                                                                                                                                           // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
inline qint64 getIndexOfColon(const QString &line)                                                                                                                                                       // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    qint64 index = 0;                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    do                                                                                                                                                                                                   // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        index = line.indexOf(':', index);                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        if (index < 0)                                                                                                                                                                                   // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            break;                                                                                                                                                                                       // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        if (                                                                                                                                                                                             // Colorize: green
            index + 1 < line.length()                                                                                                                                                                    // Colorize: green
            &&                                                                                                                                                                                           // Colorize: green
            line.at(index + 1) == '\''                                                                                                                                                                   // Colorize: green
           )                                                                                                                                                                                             // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            ++index;                                                                                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            continue;                                                                                                                                                                                    // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        if (                                                                                                                                                                                             // Colorize: green
            index + 1 >= line.length()                                                                                                                                                                   // Colorize: green
            ||                                                                                                                                                                                           // Colorize: green
            line.at(index + 1) != ':'                                                                                                                                                                    // Colorize: green
           )                                                                                                                                                                                             // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            break;                                                                                                                                                                                       // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        index += 2;                                                                                                                                                                                      // Colorize: green
    } while(true);                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return index;                                                                                                                                                                                        // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
void CppSwitchVerifier::verify(CodeWorkerThread *worker, const QString &path, const QString &/*content*/, const QStringList &lines)                                                                      // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    for (qint64 i = 0; i < lines.size(); ++i)                                                                                                                                                            // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        QString line = lines.at(i);                                                                                                                                                                      // Colorize: green
        VERIFIER_IGNORE(line, "// Ignore CppSwitchVerifier");                                                                                                                                            // Colorize: green
        removeComments(line);                                                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        QString lineTrimmed = line.trimmed();                                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        if (lineTrimmed.startsWith("switch ("))                                                                                                                                                          // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            if (lineTrimmed.endsWith(')'))                                                                                                                                                               // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                QString spaces = line.left(line.indexOf("switch ("));                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                if (                                                                                                                                                                                     // Colorize: green
                    i + 1 < lines.size()                                                                                                                                                                 // Colorize: green
                    &&                                                                                                                                                                                   // Colorize: green
                    lines.at(i + 1) == spaces + '{'                                                                                                                                                      // Colorize: green
                   )                                                                                                                                                                                     // Colorize: green
                {                                                                                                                                                                                        // Colorize: green
                    qint64 startLine = i + 2;                                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                    // Find the end of switch                                                                                                                                                            // Colorize: green
                    {                                                                                                                                                                                    // Colorize: green
                        i = startLine;                                                                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                        while (i < lines.size() && lines.at(i) != spaces + '}')                                                                                                                          // Colorize: green
                        {                                                                                                                                                                                // Colorize: green
                            ++i;                                                                                                                                                                         // Colorize: green
                        }                                                                                                                                                                                // Colorize: green
                    }                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                    if (i < lines.size())                                                                                                                                                                // Colorize: green
                    {                                                                                                                                                                                    // Colorize: green
                        qint64 endLine = i - 1;                                                                                                                                                          // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                        bool defaultCaseFound = false;                                                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                        // Search for default case and verify syntax                                                                                                                                     // Colorize: green
                        {                                                                                                                                                                                // Colorize: green
                            for (qint64 j = startLine; j <= endLine; ++j)                                                                                                                                // Colorize: green
                            {                                                                                                                                                                            // Colorize: green
                                QString switchLine = lines.at(j);                                                                                                                                        // Colorize: green
                                removeComments(switchLine);                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                QString switchLineTrimmed = switchLine.trimmed();                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                if (                                                                                                                                                                     // Colorize: green
                                    switchLineTrimmed.startsWith("case ")                                                                                                                                // Colorize: green
                                    ||                                                                                                                                                                   // Colorize: green
                                    switchLineTrimmed.startsWith("default:")                                                                                                                             // Colorize: green
                                   )                                                                                                                                                                     // Colorize: green
                                {                                                                                                                                                                        // Colorize: green
                                    // Check for default case position                                                                                                                                   // Colorize: green
                                    {                                                                                                                                                                    // Colorize: green
                                        if (switchLineTrimmed.startsWith("default:"))                                                                                                                    // Colorize: green
                                        {                                                                                                                                                                // Colorize: green
                                            if (defaultCaseFound)                                                                                                                                        // Colorize: green
                                            {                                                                                                                                                            // Colorize: green
                                                worker->addError(path, j, "Two default cases");                                                                                                          // Colorize: green
                                            }                                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                            defaultCaseFound = true;                                                                                                                                     // Colorize: green
                                        }                                                                                                                                                                // Colorize: green
                                        else                                                                                                                                                             // Colorize: green
                                        {                                                                                                                                                                // Colorize: green
                                            if (defaultCaseFound)                                                                                                                                        // Colorize: green
                                            {                                                                                                                                                            // Colorize: green
                                                worker->addError(path, j, "default case should be last");                                                                                                // Colorize: green
                                            }                                                                                                                                                            // Colorize: green
                                        }                                                                                                                                                                // Colorize: green
                                    }                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                    // Check for syntax of single case                                                                                                                                   // Colorize: green
                                    {                                                                                                                                                                    // Colorize: green
                                        QString tail = switchLineTrimmed.mid(getIndexOfColon(switchLineTrimmed) + 1).trimmed();                                                                          // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                        if (                                                                                                                                                             // Colorize: green
                                            !tail.startsWith("return ")                                                                                                                                  // Colorize: green
                                            &&                                                                                                                                                           // Colorize: green
                                            !tail.endsWith("break;")                                                                                                                                     // Colorize: green
                                           )                                                                                                                                                             // Colorize: green
                                        {                                                                                                                                                                // Colorize: green
                                            QString switchSpaces = switchLine.left(switchLine.indexOf(switchLineTrimmed));                                                                               // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                            if (                                                                                                                                                         // Colorize: green
                                                j + 1 <= endLine                                                                                                                                         // Colorize: green
                                                &&                                                                                                                                                       // Colorize: green
                                                lines.at(j + 1) == switchSpaces + '{'                                                                                                                    // Colorize: green
                                               )                                                                                                                                                         // Colorize: green
                                            {                                                                                                                                                            // Colorize: green
                                                // Check for blank line                                                                                                                                  // Colorize: green
                                                {                                                                                                                                                        // Colorize: green
                                                    if (                                                                                                                                                 // Colorize: green
                                                        j > startLine                                                                                                                                    // Colorize: green
                                                        &&                                                                                                                                               // Colorize: green
                                                        (                                                                                                                                                // Colorize: green
                                                         lines.at(j - 1).contains("break;")                                                                                                              // Colorize: green
                                                         ||                                                                                                                                              // Colorize: green
                                                         lines.at(j - 1).contains("return ")                                                                                                             // Colorize: green
                                                        )                                                                                                                                                // Colorize: green
                                                       )                                                                                                                                                 // Colorize: green
                                                    {                                                                                                                                                    // Colorize: green
                                                        worker->addError(path, j, "Blank line missed between cases");                                                                                    // Colorize: green
                                                    }                                                                                                                                                    // Colorize: green
                                                }                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                // Skip to case end                                                                                                                                      // Colorize: green
                                                {                                                                                                                                                        // Colorize: green
                                                    while (j <= endLine && lines.at(j) != switchSpaces + '}')                                                                                            // Colorize: green
                                                    {                                                                                                                                                    // Colorize: green
                                                        ++j;                                                                                                                                             // Colorize: green
                                                    }                                                                                                                                                    // Colorize: green
                                                }                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                if (j <= endLine)                                                                                                                                        // Colorize: green
                                                {                                                                                                                                                        // Colorize: green
                                                    // Check for break at the end and for blank line                                                                                                     // Colorize: green
                                                    {                                                                                                                                                    // Colorize: green
                                                        if (                                                                                                                                             // Colorize: green
                                                            j + 1 <= endLine                                                                                                                             // Colorize: green
                                                            &&                                                                                                                                           // Colorize: green
                                                            lines.at(j + 1) == switchSpaces + "break;"                                                                                                   // Colorize: green
                                                           )                                                                                                                                             // Colorize: green
                                                        {                                                                                                                                                // Colorize: green
                                                            ++j;                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                            if (j + 1 <= endLine)                                                                                                                        // Colorize: green
                                                            {                                                                                                                                            // Colorize: green
                                                                if (lines.at(j + 1).trimmed() == "")                                                                                                     // Colorize: green
                                                                {                                                                                                                                        // Colorize: green
                                                                    ++j;                                                                                                                                 // Colorize: green
                                                                }                                                                                                                                        // Colorize: green
                                                                else                                                                                                                                     // Colorize: green
                                                                {                                                                                                                                        // Colorize: green
                                                                    worker->addError(path, j, "Expected new line after break statement");                                                                // Colorize: green
                                                                }                                                                                                                                        // Colorize: green
                                                            }                                                                                                                                            // Colorize: green
                                                        }                                                                                                                                                // Colorize: green
                                                        else                                                                                                                                             // Colorize: green
                                                        {                                                                                                                                                // Colorize: green
                                                            worker->addError(path, j, "Expected break statement after closing bracket }");                                                               // Colorize: green
                                                        }                                                                                                                                                // Colorize: green
                                                    }                                                                                                                                                    // Colorize: green
                                                }                                                                                                                                                        // Colorize: green
                                                else                                                                                                                                                     // Colorize: green
                                                {                                                                                                                                                        // Colorize: green
                                                    worker->addError(path, j, "Expected closing bracket }");                                                                                             // Colorize: green
                                                }                                                                                                                                                        // Colorize: green
                                            }                                                                                                                                                            // Colorize: green
                                            else                                                                                                                                                         // Colorize: green
                                            {                                                                                                                                                                // Colorize: green
                                                if (                                                                                                                                                         // Colorize: green
                                                    j + 1 > endLine                                                                                                                                          // Colorize: green
                                                    ||                                                                                                                                                       // Colorize: green
                                                    !lines.at(j + 1).startsWith(switchSpaces + "case ")                                                                                                      // Colorize: green
                                                   )                                                                                                                                                         // Colorize: green
                                                {                                                                                                                                                            // Colorize: green
                                                    worker->addError(path, j, "Expected opening bracket {");                                                                                                 // Colorize: green
                                                }                                                                                                                                                            // Colorize: green
                                            }                                                                                                                                                                // Colorize: green
                                        }                                                                                                                                                                // Colorize: green
                                    }                                                                                                                                                                    // Colorize: green
                                }                                                                                                                                                                        // Colorize: green
                                else                                                                                                                                                                     // Colorize: green
                                {                                                                                                                                                                        // Colorize: green
                                    if (                                                                                                                                                                 // Colorize: green
                                        switchLineTrimmed != ""                                                                                                                                          // Colorize: green
                                        ||                                                                                                                                                               // Colorize: green
                                        j <= startLine                                                                                                                                                   // Colorize: green
                                        ||                                                                                                                                                               // Colorize: green
                                        (                                                                                                                                                                // Colorize: green
                                         !lines.at(j - 1).contains("break;")                                                                                                                             // Colorize: green
                                         &&                                                                                                                                                              // Colorize: green
                                         !lines.at(j - 1).contains("return ")                                                                                                                            // Colorize: green
                                        )                                                                                                                                                                // Colorize: green
                                       )                                                                                                                                                                 // Colorize: green
                                    {                                                                                                                                                                    // Colorize: green
                                        worker->addError(path, j, "Unexpected line in switch statement");                                                                                                // Colorize: green
                                    }                                                                                                                                                                    // Colorize: green
                                }                                                                                                                                                                        // Colorize: green
                            }                                                                                                                                                                            // Colorize: green
                        }                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                        if (!defaultCaseFound)                                                                                                                                                           // Colorize: green
                        {                                                                                                                                                                                // Colorize: green
                            worker->addError(path, endLine + 1, "default case not found");                                                                                                               // Colorize: green
                        }                                                                                                                                                                                // Colorize: green
                    }                                                                                                                                                                                    // Colorize: green
                    else                                                                                                                                                                                 // Colorize: green
                    {                                                                                                                                                                                    // Colorize: green
                        worker->addError(path, i, "Unexpected switch definition");                                                                                                                       // Colorize: green
                    }                                                                                                                                                                                    // Colorize: green
                }                                                                                                                                                                                        // Colorize: green
                else                                                                                                                                                                                     // Colorize: green
                {                                                                                                                                                                                        // Colorize: green
                    worker->addError(path, i, "Unexpected switch definition");                                                                                                                           // Colorize: green
                }                                                                                                                                                                                        // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
            else                                                                                                                                                                                         // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                worker->addError(path, i, "Unexpected switch definition");                                                                                                                               // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
CppSwitchVerifier cppSwitchVerifierInstance;                                                                                                                                                             // Colorize: green
