CppTypesVerifier::CppTypesVerifier()                                                                                                                                                                     // Colorize: green
    : BaseCodeVerifier(VERIFICATION_COMMON_CPP)                                                                                                                                                          // Colorize: green
    , mDefinitionRegExp("\\b(((un)?signed +(char|short|int|long)?)|char|short|int|long|char8_t|char16_t|char32_t|wchar_t)\\b") // unsigned ; singed char; char16_t                                        // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // Nothing                                                                                                                                                                                           // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
void CppTypesVerifier::verify(CodeWorkerThread *worker, const QString &path, const QString &/*content*/, const QStringList &lines)                                                                       // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // Do not check specific files                                                                                                                                                                       // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        if (path.contains("/src/os/shared/common/src/com/ngos/shared/common/ngos/types.h"))                                                                                                              // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            return;                                                                                                                                                                                      // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    for (qint64 i = 0; i < lines.size(); ++i)                                                                                                                                                            // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        QString line = lines.at(i);                                                                                                                                                                      // Colorize: green
        VERIFIER_IGNORE(line, "// Ignore CppTypesVerifier");                                                                                                                                             // Colorize: green
        removeComments(line);                                                                                                                                                                            // Colorize: green
        removeStrings(line);                                                                                                                                                                             // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        QRegularExpressionMatchIterator matches = mDefinitionRegExp.globalMatch(line);                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        while (matches.hasNext())                                                                                                                                                                        // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            QRegularExpressionMatch match = matches.next();                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            if (path.contains("/tools/qt/"))                                                                                                                                                             // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                QString type = match.captured(0);                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                if (                                                                                                                                                                                     // Colorize: green
                    type != "char"                                                                                                                                                                       // Colorize: green
                    &&                                                                                                                                                                                   // Colorize: green
                    type != "wchar_t"                                                                                                                                                                    // Colorize: green
                   )                                                                                                                                                                                     // Colorize: green
                {                                                                                                                                                                                        // Colorize: green
                    worker->addWarning(path, i, "Please use more formal data type: quint8/qint8/quint16/qint16/quint32/qint32/quint64/qint64");                                                          // Colorize: green
                }                                                                                                                                                                                        // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
            else                                                                                                                                                                                         // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                worker->addWarning(path, i, "Please use more formal data type: u8/i8/u16/i16/u32/i32/u64/i64/char8/char16");                                                                             // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
