#include "mdlinkmarkersverifier.h"                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
#include <QDir>                                                                                                                                                                                          // Colorize: green
#include <QFile>                                                                                                                                                                                         // Colorize: green
#include <QFileInfo>                                                                                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
#include <com/ngos/devtools/docs_verifier/other/docsverificationfiletype.h>                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
MdLinksMarkersVerifier::MdLinksMarkersVerifier()                                                                                                                                                         // Colorize: green
    : BaseDocsVerifier(DocsVerificationFileType::MD)                                                                                                                                                     // Colorize: green
    , mLinkRegExp("\\[([^\\]]*)\\]\\(([^)]*)\\)")                                                                                                                                                        // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // Nothing                                                                                                                                                                                           // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
void MdLinksMarkersVerifier::verify(DocsWorkerThread *worker, const QString &path, const QStringList &lines)                                                                                             // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    QString parentFolder = path.left(path.lastIndexOf('/') + 1);                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    for (qint64 i = 0; i < lines.size(); ++i)                                                                                                                                                            // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        QString line = lines.at(i);                                                                                                                                                                      // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        QRegularExpressionMatchIterator matches = mLinkRegExp.globalMatch(line);                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        while (matches.hasNext())                                                                                                                                                                        // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            QRegularExpressionMatch match = matches.next();                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            QString linkText = match.captured(1).trimmed();                                                                                                                                              // Colorize: green
            QString linkUrl  = match.captured(2).trimmed();                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            QString linkUrlOriginal = linkUrl;                                                                                                                                                           // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            if (linkText == "")                                                                                                                                                                          // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                worker->addError(path, i, QString("Empty text specified for link: %1").arg(linkUrlOriginal));                                                                                            // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            if (                                                                                                                                                                                         // Colorize: green
                !linkUrl.startsWith("http://") // Ignore CppPunctuationVerifier                                                                                                                          // Colorize: green
                &&                                                                                                                                                                                       // Colorize: green
                !linkUrl.startsWith("https://") // Ignore CppPunctuationVerifier                                                                                                                         // Colorize: green
               )                                                                                                                                                                                         // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                QString linkMarker;                                                                                                                                                                      // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                qint64 index2 = linkUrl.indexOf('#');                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                if (index2 >= 0)                                                                                                                                                                         // Colorize: green
                {                                                                                                                                                                                        // Colorize: green
                    linkMarker = linkUrl.mid(index2 + 1);                                                                                                                                                // Colorize: green
                    linkUrl    = linkUrl.left(index2);                                                                                                                                                   // Colorize: green
                }                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                QString targetFile = parentFolder + '/' + linkUrl.replace("%20", QChar(' '));                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                if (QFileInfo(targetFile).exists())                                                                                                                                                      // Colorize: green
                {                                                                                                                                                                                        // Colorize: green
                    if (linkMarker != "")                                                                                                                                                                // Colorize: green
                    {                                                                                                                                                                                    // Colorize: green
                        QFile file(targetFile);                                                                                                                                                          // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                        if (file.open(QIODevice::ReadOnly))                                                                                                                                              // Colorize: green
                        {                                                                                                                                                                                // Colorize: green
                            QString anotherContent = QString::fromUtf8(file.readAll());                                                                                                                  // Colorize: green
                            file.close();                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                            QStringList anotherLines = anotherContent.split('\n');                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                            bool found = false;                                                                                                                                                          // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                            for (qint64 j = 0; j < anotherLines.size(); ++j)                                                                                                                             // Colorize: green
                            {                                                                                                                                                                            // Colorize: green
                                QString anotherLine = anotherLines.at(j).trimmed();                                                                                                                      // Colorize: green
                                QString sectionName;                                                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                if (anotherLine.startsWith('#'))                                                                                                                                         // Colorize: green
                                {                                                                                                                                                                        // Colorize: green
                                    sectionName = anotherLine;                                                                                                                                           // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                    while (sectionName.startsWith('#'))                                                                                                                                  // Colorize: green
                                    {                                                                                                                                                                    // Colorize: green
                                        sectionName.remove(0, 1);                                                                                                                                        // Colorize: green
                                    }                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                    sectionName = sectionName.trimmed();                                                                                                                                 // Colorize: green
                                }                                                                                                                                                                        // Colorize: green
                                else                                                                                                                                                                     // Colorize: green
                                if (                                                                                                                                                                     // Colorize: green
                                    j < anotherLines.size() - 1                                                                                                                                          // Colorize: green
                                    &&                                                                                                                                                                   // Colorize: green
                                    (                                                                                                                                                                    // Colorize: green
                                     (                                                                                                                                                                   // Colorize: green
                                      anotherLines.at(j + 1).startsWith('-')                                                                                                                             // Colorize: green
                                      &&                                                                                                                                                                 // Colorize: green
                                      anotherLines.at(j + 1) == QString(anotherLine.length(), '-')                                                                                                       // Colorize: green
                                     )                                                                                                                                                                   // Colorize: green
                                     ||                                                                                                                                                                  // Colorize: green
                                     (                                                                                                                                                                   // Colorize: green
                                      anotherLines.at(j + 1).startsWith('=')                                                                                                                             // Colorize: green
                                      &&                                                                                                                                                                 // Colorize: green
                                      anotherLines.at(j + 1) == QString(anotherLine.length(), '=')                                                                                                       // Colorize: green
                                     )                                                                                                                                                                   // Colorize: green
                                    )                                                                                                                                                                    // Colorize: green
                                   )                                                                                                                                                                     // Colorize: green
                                {                                                                                                                                                                        // Colorize: green
                                    sectionName = anotherLine;                                                                                                                                           // Colorize: green
                                }                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                if (sectionName != "")                                                                                                                                                   // Colorize: green
                                {                                                                                                                                                                        // Colorize: green
                                    sectionName = sectionName.toLower().replace(' ', '-').remove('.').remove('=');                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                    if (sectionName == linkMarker)                                                                                                                                       // Colorize: green
                                    {                                                                                                                                                                    // Colorize: green
                                        found = true;                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                        break;                                                                                                                                                           // Colorize: green
                                    }                                                                                                                                                                    // Colorize: green
                                }                                                                                                                                                                        // Colorize: green
                            }                                                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                            if (!found)                                                                                                                                                                  // Colorize: green
                            {                                                                                                                                                                            // Colorize: green
                                worker->addError(path, i, QString("Invalid marker specified in link: %1").arg(linkUrlOriginal));                                                                         // Colorize: green
                            }                                                                                                                                                                            // Colorize: green
                        }                                                                                                                                                                                // Colorize: green
                        else                                                                                                                                                                             // Colorize: green
                        {                                                                                                                                                                                // Colorize: green
                            worker->addError(path, i, QString("Invalid link specified: %1").arg(linkUrlOriginal));                                                                                       // Colorize: green
                        }                                                                                                                                                                                // Colorize: green
                    }                                                                                                                                                                                    // Colorize: green
                }                                                                                                                                                                                        // Colorize: green
                else                                                                                                                                                                                     // Colorize: green
                {                                                                                                                                                                                        // Colorize: green
                    worker->addError(path, i, QString("Invalid link specified: %1").arg(linkUrlOriginal));                                                                                               // Colorize: green
                }                                                                                                                                                                                        // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
MdLinksMarkersVerifier mdLinksMarkersVerifierInstance;                                                                                                                                                   // Colorize: green
