typedef quint16 pe_optional_header_dll_characteristics_flags;                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
enum class PeOptionalHeaderDllCharacteristicsFlag: pe_optional_header_dll_characteristics_flags                                                                                                          // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    NONE                  = 0,                                                                                                                                                                           // Colorize: green
    HIGH_ENTROPY_VA       = (1ULL << 5),  // Image can handle a high entropy 64-bit virtual address space.                                                                                               // Colorize: green
    DYNAMIC_BASE          = (1ULL << 6),  // DLL can be relocated at load time.                                                                                                                          // Colorize: green
    FORCE_INTEGRITY       = (1ULL << 7),  // Code Integrity checks are enforced.                                                                                                                         // Colorize: green
    NX_COMPAT             = (1ULL << 8),  // Image is NX compatible.                                                                                                                                     // Colorize: green
    NO_ISOLATION          = (1ULL << 9),  // Isolation aware, but do not isolate the image.                                                                                                              // Colorize: green
    NO_SEH                = (1ULL << 10), // Does not use structured exception (SE) handling. No SE handler may be called in this image.                                                                 // Colorize: green
    NO_BIND               = (1ULL << 11), // Do not bind the image.                                                                                                                                      // Colorize: green
    APPCONTAINER          = (1ULL << 12), // Image must execute in an AppContainer.                                                                                                                      // Colorize: green
    WDM_DRIVER            = (1ULL << 13), // A WDM driver.                                                                                                                                               // Colorize: green
    GUARD_CF              = (1ULL << 14), // Image supports Control Flow Guard.                                                                                                                          // Colorize: green
    TERMINAL_SERVER_AWARE = (1ULL << 15)  // Terminal Server aware.                                                                                                                                      // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
