enum class PeOptionalHeaderSubsystem: quint16                                                                                                                                                            // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    UNKNOWN                  = 0,      // An unknown subsystem                                                                                                                                           // Colorize: green
    NATIVE                   = 0x0001, // Device drivers and native Windows processes                                                                                                                    // Colorize: green
    WINDOWS_GUI              = 0x0002, // The Windows graphical user interface (GUI) subsystem                                                                                                           // Colorize: green
    WINDOWS_CUI              = 0x0003, // The Windows character subsystem                                                                                                                                // Colorize: green
    OS2_CUI                  = 0x0005, // The OS/2 character subsystem                                                                                                                                   // Colorize: green
    POSIX_CUI                = 0x0007, // The Posix character subsystem                                                                                                                                  // Colorize: green
    NATIVE_WINDOWS           = 0x0008, // Native Win9x driver                                                                                                                                            // Colorize: green
    WINDOWS_CE_GUI           = 0x0009, // Windows CE                                                                                                                                                     // Colorize: green
    UEFI_APPLICATION         = 0x000A, // An Extensible Firmware Interface (EFI) application                                                                                                             // Colorize: green
    UEFI_BOOT_SERVICE_DRIVER = 0x000B, // An EFI driver with boot services                                                                                                                               // Colorize: green
    UEFI_RUNTIME_DRIVER      = 0x000C, // An EFI driver with run-time services                                                                                                                           // Colorize: green
    UEFI_ROM                 = 0x000D, // An EFI ROM image                                                                                                                                               // Colorize: green
    XBOX                     = 0x000E, // XBOX                                                                                                                                                           // Colorize: green
    WINDOWS_BOOT_APPLICATION = 0x0010  // Windows boot application.                                                                                                                                      // Colorize: green
};                                                                                                                                                                                                       // Colorize: green
