void DataDirectory::print(DataDirectoryType type)                                                                                                                                                                              // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    Console::out(QString("Data Directory (%1):").arg(enumToString(type)));                                                                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    Console::out(QString("    virtualAddress: 0x%1").arg(virtualAddress, 8, 16, QChar('0')));                                                                                                                                                                                      // Colorize: green
    Console::out(QString("    size:           0x%1").arg(size,           8, 16, QChar('0')));                                                                                                                                                                                      // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    Console::out("");                                                                                                                                                                                    // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
bool DataDirectory::verify(DataDirectoryType type)                                                                                                                                                                             // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // Check virtualAddress                                                                                                                                                                              // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        if (virtualAddress != 0)                                                                                                                                                                         // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            Console::err(QString("Wrong Data Directory %1 virtualAddress: 0x%2")                                                                                                                            // Colorize: green
                                    .arg(enumToFullString(type))                                                                                                                                         // Colorize: green
                                    .arg(virtualAddress, 8, 16, QChar('0'))                                                                                                                              // Colorize: green
            );                                                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            return false;                                                                                                                                                                                // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    // Check size                                                                                                                                                                                        // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        if (size != 0)                                                                                                                                                                                   // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            Console::err(QString("Wrong Data Directory %1 size: 0x%2")                                                                                                                                      // Colorize: green
                                    .arg(enumToFullString(type))                                                                                                                                         // Colorize: green
                                    .arg(size, 8, 16, QChar('0'))                                                                                                                                        // Colorize: green
            );                                                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            return false;                                                                                                                                                                                // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return true;                                                                                                                                                                                         // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
