#include <QCoreApplication>                                                                                                                                                                              // Colorize: green
#include <QDateTime>                                                                                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
#include <com/ngos/devtools/shared/console/console.h>                                                                                                                                                    // Colorize: green
#include <com/ngos/devtools/specs_verifier/threads/specverifythread.h>                                                                                                                                 // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
void usage()                                                                                                                                                                                             // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    // Ignore CppAlignmentVerifier [BEGIN]                                                                                                                                                               // Colorize: green
    Console::err(                                                                                                                                                                                        // Colorize: green
                "Usage: specs_verifier PATH\n"                                                                                                                                                            // Colorize: green
                "    * PATH - path to folder"                                                                                                                                                            // Colorize: green
                );                                                                                                                                                                                       // Colorize: green
    // Ignore CppAlignmentVerifier [END]                                                                                                                                                                 // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
qint32 main(qint32 argc, char *argv[])                                                                                                                                                                   // Colorize: green
{                                                                                                                                                                                                        // Colorize: green
    QCoreApplication app(argc, argv);                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    const QStringList &arguments = app.arguments();                                                                                                                                                             // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    // Check arguments                                                                                                                                                                                   // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        if (arguments.size() != 2)                                                                                                                                                                       // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            usage();                                                                                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            return 1;                                                                                                                                                                                    // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    QString targetPath = arguments.at(1);                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    // Output application info                                                                                                                                                                           // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        Console::out("Specs verifier started");                                                                                                                                                           // Colorize: green
        Console::out("");                                                                                                                                                                                // Colorize: green
        Console::out("Parameters:");                                                                                                                                                                     // Colorize: green
        Console::out(QString("PATH = %1")                                                                                                                                                                // Colorize: green
                                .arg(targetPath)                                                                                                                                                         // Colorize: green
                     );                                                                                                                                                                                  // Colorize: green
        Console::out("");                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    qint64 startTime = QDateTime::currentMSecsSinceEpoch();                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    QList<SpecVerifyThread *> verifiers;                                                                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    // Start workers                                                                                                                                                                                     // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        for (qint64 i = 0; i < QThread::idealThreadCount(); ++i)                                                                                                                                         // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            SpecVerifyThread *worker = new SpecVerifyThread(targetPath);                                                                                                                                           // Colorize: green
            worker->start();                                                                                                                                                                             // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            verifiers.append(worker);                                                                                                                                                                      // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    QStringList messages;                                                                                                                                                                                // Colorize: green
    QStringList errors;                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    // Wait for verifiers and get messages from them                                                                                                                                                     // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        for (qint64 i = 0; i < verifiers.size(); ++i)                                                                                                                                                    // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            SpecVerifyThread *verifier = verifiers.at(i);                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            verifier->wait();                                                                                                                                                                            // Colorize: green
            messages.append(verifier->getMessages());                                                                                                                                                    // Colorize: green
            errors.append(verifier->getErrors());                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            delete verifier;                                                                                                                                                                             // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    // Print messages                                                                                                                                                                                    // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        messages.sort();                                                                                                                                                                                 // Colorize: green
                                                                                                                                                                                                 // Colorize: green
        for (qint64 i = 0; i < messages.size(); ++i)                                                                                                                                             // Colorize: green
        {                                                                                                                                                                                        // Colorize: green
            Console::out(messages.at(i));                                                                                                                                             // Colorize: green
        }                                                                                                                                                                                        // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    // If there is some error then print errors and exit with error code                                                                                                                                 // Colorize: green
    if (!errors.isEmpty())                                                                                                                                                                               // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        errors.sort();                                                                                                                                                                                   // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        Console::err("");                                                                                                                                                                                // Colorize: green
        Console::err("Errors:");                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        for (qint64 i = 0; i < errors.size(); ++i)                                                                                                                                                     // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            Console::err(errors.at(i));                                                                                                                                                               // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        Console::out("");                                                                                                                                                                                // Colorize: green
        Console::out(QString("Source code verified in %1 ms")                                                                                                                                            // Colorize: green
                                .arg(QDateTime::currentMSecsSinceEpoch() - startTime)                                                                                                                    // Colorize: green
                     );                                                                                                                                                                                  // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        return 1;                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    Console::out("");                                                                                                                                                                                // Colorize: green
    Console::out(QString("Source code verified in %1 ms")                                                                                                                                                // Colorize: green
                            .arg(QDateTime::currentMSecsSinceEpoch() - startTime)                                                                                                                        // Colorize: green
                 );                                                                                                                                                                                      // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    return 0;                                                                                                                                                                                            // Colorize: green
}                                                                                                                                                                                                        // Colorize: green
