        checkCpuid00000004Eax(thread, specContent);                                                                                                                                                      // Colorize: green
        checkCpuidCacheType(thread, specContent);                                                                                                                                                        // Colorize: green
        checkCpuid0000000bEbx0(thread, specContent);                                                                                                                                                     // Colorize: green
