#!/bin/bash                                                                                                                                                                                              # Colorize: green
                                                                                                                                                                                                         # Colorize: green
# This script helps to start VM                                                                                                                                                                          # Colorize: green
# Author: Maxim Shvecov                                                                                                                                                                                  # Colorize: green
# Usage: ./start.sh RAM_SIZE [DISPLAY_TYPE] [OS_TYPE]                                                                                                                                                    # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
###########################################################################################                                                                                                              # Colorize: green
#    PARAMETERS                                                                                                                                                                                          # Colorize: green
###########################################################################################                                                                                                              # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
CURRENT_PATH=`pwd`                                                                                                                                                                                       # Colorize: green
VM_NAME="NGOS_dev"                                                                                                                                                                                       # Colorize: green
RAM_SIZE=$1                                                                                                                                                                                              # Colorize: green
DISPLAY_TYPE=$2                                                                                                                                                                                          # Colorize: green
OS_TYPE=$3                                                                                                                                                                                               # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
###########################################################################################                                                                                                              # Colorize: green
#    VERIFICATION                                                                                                                                                                                        # Colorize: green
###########################################################################################                                                                                                              # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
if [ "${RAM_SIZE}" == "" ]; then                                                                                                                                                                         # Colorize: green
    echo "Please specify RAM size."                                                                                                                                                                      # Colorize: green
    echo "    Usage: ./start.sh RAM_SIZE [DISPLAY_TYPE] [OS_TYPE]"                                                                                                                                       # Colorize: green
                                                                                                                                                                                                         # Colorize: green
    exit 1                                                                                                                                                                                               # Colorize: green
fi                                                                                                                                                                                                       # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
if [ "${DISPLAY_TYPE}" == "" ]; then                                                                                                                                                                     # Colorize: green
    DISPLAY_TYPE=vnc                                                                                                                                                                                     # Colorize: green
fi                                                                                                                                                                                                       # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
if [ "${OS_TYPE}" == "" ]; then                                                                                                                                                                          # Colorize: green
    OS_TYPE=ngos                                                                                                                                                                                         # Colorize: green
fi                                                                                                                                                                                                       # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
###########################################################################################                                                                                                              # Colorize: green
#    PROCESSING                                                                                                                                                                                          # Colorize: green
###########################################################################################                                                                                                              # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
sudo virsh destroy  "${VM_NAME}"         2> /dev/null                                                                                                                                                    # Colorize: green
sudo virsh undefine "${VM_NAME}" --nvram 2> /dev/null                                                                                                                                                    # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
sleep 3                                                                                                                                                                                                  # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
cd ../                                                                                                                                                                                                   # Colorize: green
./make_hdd.sh ${OS_TYPE} || exit 1                                                                                                                                                                       # Colorize: green
cd ${CURRENT_PATH}/                                                                                                                                                                                      # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
mkdir -p ../../../build/disks/ 2> /dev/null                                                                                                                                                              # Colorize: green
sudo rm "../../../build/disks/${VM_NAME}.raw" 2> /dev/null                                                                                                                                               # Colorize: green
qemu-img convert -O raw "../../../build/disks/${VM_NAME}.img" "../../../build/disks/${VM_NAME}.raw" 2> /dev/null                                                                                         # Colorize: green
rm "../../../build/disks/${VM_NAME}.img"                                                                                                                                                                 # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
sudo virt-install --name ${VM_NAME} \                                                                                                                                                                    # Colorize: green
    --connect qemu:///system \                                                                                                                                                                           # Colorize: green
    --virt-type qemu \                                                                                                                                                                                   # Colorize: green
    --arch=x86_64 \                                                                                                                                                                                      # Colorize: green
    --os-variant=none \                                                                                                                                                                                  # Colorize: green
    --ram ${RAM_SIZE} \                                                                                                                                                                                  # Colorize: green
    --vcpus 4,sockets=1,cores=2,threads=2 \                                                                                                                                                              # Colorize: green
    --cpu Icelake-Client,+la57 \                                                                                                                                                                         # Colorize: green
    --graphics ${DISPLAY_TYPE} \                                                                                                                                                                         # Colorize: green
    --video=vga \                                                                                                                                                                                        # Colorize: green
    --network network=default,model=virtio \                                                                                                                                                             # Colorize: green
    --disk path="../../../build/disks/${VM_NAME}.raw",format=raw,bus=virtio,cache=none \                                                                                                                 # Colorize: green
    --serial unix,path=/tmp/qemuttyS0 \                                                                                                                                                                  # Colorize: green
    --boot uefi \                                                                                                                                                                                        # Colorize: green
    --qemu-commandline="-gdb tcp::1234" \                                                                                                                                                                # Colorize: green
    --noautoconsole                                                                                                                                                                                      # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
sudo virsh start "${VM_NAME}" 2> /dev/null                                                                                                                                                               # Colorize: green
                                                                                                                                                                                                         # Colorize: green
if [ "${DISPLAY_TYPE}" != "none" ]; then                                                                                                                                                                 # Colorize: green
    virt-viewer -c qemu:///system "${VM_NAME}" &                                                                                                                                                         # Colorize: green
fi                                                                                                                                                                                                       # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
echo "NGOS QEMU VM started"                                                                                                                                                                              # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
NGOS_LOGGER_PID=`ps -ef | grep "tail -f -n +1 logs/" | grep -v grep | awk '{print $2}'`                                                                                                                  # Colorize: green
kill -9 ${NGOS_LOGGER_PID} > /dev/null 2>&1                                                                                                                                                              # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
sudo socat unix-connect:/tmp/qemuttyS0 pty,link=/tmp/qemuttyS0-pty,raw,echo=0 &                                                                                                                          # Colorize: green
sleep 1                                                                                                                                                                                                  # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
mkdir logs/ 2> /dev/null                                                                                                                                                                                 # Colorize: green
sudo cat /tmp/qemuttyS0-pty > "logs/${VM_NAME}.log" &                                                                                                                                                    # Colorize: green
sleep 1                                                                                                                                                                                                  # Colorize: green
                                                                                                                                                                                                         # Colorize: green
tail -f -n +1 "logs/${VM_NAME}.log" |                                                                                                                                                                    # Colorize: green
    while IFS= read -r line                                                                                                                                                                              # Colorize: green
    do                                                                                                                                                                                                   # Colorize: green
        echo "${line}"                                                                                                                                                                                   # Colorize: green
    done                                                                                                                                                                                                 # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
                                                                                                                                                                                                         # Colorize: green
exit 0                                                                                                                                                                                                   # Colorize: green
