<?php                                                                                                                                                                                                    // Colorize: green
    require_once "../common/functions.php";                                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    function handle_request()                                                                                                                                                                            // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        switch ($_SERVER["REQUEST_METHOD"])                                                                                                                                                              // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            case "GET":                                                                                                                                                                                  // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                handle_get();                                                                                                                                                                            // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
            break;                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            case "POST":                                                                                                                                                                                 // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                handle_post();                                                                                                                                                                           // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
            break;                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            default:                                                                                                                                                                                     // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                die("Unknown method");                                                                                                                                                                   // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
            break;                                                                                                                                                                                       // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    function handle_get()                                                                                                                                                                                // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        // Nothing                                                                                                                                                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    function insert_app_version($link, $data, $app_version_id, $app_id, $version, $secret_key) // TODO: Do we need secret_key                                                                            // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        // Ignore PhpAlignmentVerifier [BEGIN]                                                                                                                                                           // Colorize: green
        $sql = "INSERT INTO " . DB_TABLE_APP_VERSIONS                                                                                                                                                    // Colorize: green
            . " (id, app_id, version, hash, completed)"                                                                                                                                                  // Colorize: green
            . " VALUES("                                                                                                                                                                                 // Colorize: green
            . "  '" . $link->real_escape_string($app_version_id) . "',"                                                                                                                                  // Colorize: green
            . "  '" . $link->real_escape_string($app_id)         . "',"                                                                                                                                  // Colorize: green
            . "  '" . $link->real_escape_string($version)        . "',"                                                                                                                                  // Colorize: green
            . "  '',"                                                                                                                                                                                    // Colorize: green
            . "  '0'"                                                                                                                                                                                    // Colorize: green
            . ")";                                                                                                                                                                                       // Colorize: green
        // Ignore PhpAlignmentVerifier [END]                                                                                                                                                             // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        $result = $link->query($sql);                                                                                                                                                                    // Colorize: green
        die_if_sql_failed($result, $link, $data, $sql);                                                                                                                                                  // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    function forward_message_to_another_servers($link, $data, $level, $my_address, $my_secret_key, $app_version_id, $app_id, $version, $secret_key)                                                      // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        switch ($level)                                                                                                                                                                                  // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            case 0:                                                                                                                                                                                      // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                $replicate_data = [                                                                                                                                                                      // Colorize: green
                    "my_address"     => $my_address,                                                                                                                                                     // Colorize: green
                    "my_secret_key"  => $my_secret_key,                                                                                                                                                  // Colorize: green
                    "app_version_id" => $app_version_id,                                                                                                                                                 // Colorize: green
                    "app_id"         => $app_id,                                                                                                                                                         // Colorize: green
                    "version"        => $version,                                                                                                                                                        // Colorize: green
                    "secret_key"     => $secret_key                                                                                                                                                      // Colorize: green
                ];                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                $region_id = get_region_id($link, $data);                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                replicate_by_region($link, $data, $replicate_data, "/rest/replicate_app_version.php", $region_id, $my_address);                                                                          // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
            break;                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            case 1:                                                                                                                                                                                      // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                // Nothing                                                                                                                                                                               // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
            break;                                                                                                                                                                                       // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            default:                                                                                                                                                                                     // Colorize: green
            {                                                                                                                                                                                            // Colorize: green
                die("Unknown level");                                                                                                                                                                    // Colorize: green
            }                                                                                                                                                                                            // Colorize: green
            break;                                                                                                                                                                                       // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    function handle_post_with_params($link, $data, $level, $my_address, $my_secret_key, $your_secret_key, $app_version_id, $app_id, $version, $secret_key)                                               // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        validate_access($link, $data, $my_address, $my_secret_key, $your_secret_key);                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        validate_app_secret_key($link, $data, $app_id, $secret_key);                                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        insert_app_version($link, $data, $app_version_id, $app_id, $version, $secret_key);                                                                                                               // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        $own_address = get_server_name($link, $data);                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        forward_message_to_another_servers($link, $data, $level, $own_address, $your_secret_key, $app_version_id, $app_id, $version, $secret_key);                                                       // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    function handle_post()                                                                                                                                                                               // Colorize: green
    {                                                                                                                                                                                                    // Colorize: green
        header("Content-type: application/json");                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        $data =                                                                                                                                                                                          // Colorize: green
        [                                                                                                                                                                                                // Colorize: green
            "status" => "Failed"                                                                                                                                                                         // Colorize: green
        ];                                                                                                                                                                                               // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        $_POST = json_decode(file_get_contents("php://input"), true);                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        $level           = @$_POST["level"];                                                                                                                                                             // Colorize: green
        $my_address      = @$_POST["my_address"];                                                                                                                                                        // Colorize: green
        $my_secret_key   = @$_POST["my_secret_key"];                                                                                                                                                     // Colorize: green
        $your_secret_key = @$_POST["your_secret_key"];                                                                                                                                                   // Colorize: green
        $app_version_id  = @$_POST["app_version_id"];                                                                                                                                                    // Colorize: green
        $app_id          = @$_POST["app_id"];                                                                                                                                                            // Colorize: green
        $version         = @$_POST["version"];                                                                                                                                                           // Colorize: green
        $secret_key      = @$_POST["secret_key"];                                                                                                                                                        // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        if (                                                                                                                                                                                             // Colorize: green
            !verify_level($level)                                                                                                                                                                        // Colorize: green
            ||                                                                                                                                                                                           // Colorize: green
            !verify_address($my_address)                                                                                                                                                                 // Colorize: green
            ||                                                                                                                                                                                           // Colorize: green
            !verify_secret_key($my_secret_key)                                                                                                                                                           // Colorize: green
            ||                                                                                                                                                                                           // Colorize: green
            !verify_secret_key($your_secret_key)                                                                                                                                                         // Colorize: green
            ||                                                                                                                                                                                           // Colorize: green
            !verify_app_version_id($app_version_id)                                                                                                                                                      // Colorize: green
            ||                                                                                                                                                                                           // Colorize: green
            !verify_app_id($app_id)                                                                                                                                                                      // Colorize: green
            ||                                                                                                                                                                                           // Colorize: green
            !verify_version($version)                                                                                                                                                                    // Colorize: green
            ||                                                                                                                                                                                           // Colorize: green
            !verify_secret_key($secret_key)                                                                                                                                                              // Colorize: green
           )                                                                                                                                                                                             // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            $data["message"] = "Invalid parameters";                                                                                                                                                     // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            die(json_encode($data));                                                                                                                                                                     // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        $link = db_connect();                                                                                                                                                                            // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        if ($link)                                                                                                                                                                                       // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            handle_post_with_params($link, $data, $level, $my_address, $my_secret_key, $your_secret_key, $app_version_id, $app_id, $version, $secret_key);                                               // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            db_disconnect($link);                                                                                                                                                                        // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
        else                                                                                                                                                                                             // Colorize: green
        {                                                                                                                                                                                                // Colorize: green
            $data["message"] = "Database connection error";                                                                                                                                              // Colorize: green
                                                                                                                                                                                                         // Colorize: green
            die(json_encode($data));                                                                                                                                                                     // Colorize: green
        }                                                                                                                                                                                                // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
        $data["status"] = "OK";                                                                                                                                                                          // Colorize: green
        echo json_encode($data);                                                                                                                                                                         // Colorize: green
    }                                                                                                                                                                                                    // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
                                                                                                                                                                                                         // Colorize: green
    handle_request();                                                                                                                                                                                    // Colorize: green
?>                                                                                                                                                                                                       // Colorize: green
