if [[ `lsb_release -rs` == 20.10 ]]; then                                                                                                                                                                # Colorize: green
    ./ubuntu_20_10.sh                                                                                                                                                                                    # Colorize: green
elif [[ `lsb_release -rs` == 20.04 ]]; then                                                                                                                                                              # Colorize: green
    ./ubuntu_20_04.sh                                                                                                                                                                                    # Colorize: green
