elif [[ `lsb_release -rs` == 18.04 ]]; then                                                                                                                                                              # Colorize: green
    ./ubuntu_18_04.sh                                                                                                                                                                                    # Colorize: green
elif [[ `lsb_release -rs` == 8.2.* ]]; then                                                                                                                                                              # Colorize: green
